import { Component, Input, OnInit } from '@angular/core';
import { IWeatherCard } from '../../models/weather';

@Component({
  selector: 'app-weather-card',
  templateUrl: './weather-card.component.html',
  styleUrls: ['./weather-card.component.css'],
})
export class WeatherCardComponent implements OnInit {
  @Input() weatherCard: IWeatherCard;

  ngOnInit(): void {
    console.log(this.weatherCard);
  }
}
<div class="weather-card"  >
  <div class="weather-card__inner">
    <div class="weather-card__dName">
      {{ weatherCard.dayDate.toDateString().split(" ")[0] }}
    </div>
    <div class="weather-card__dImg">
      <img src="" alt="" />
    </div>
    <div class="weather-card__infoTemp">
      <div class="weather-card__temp-min">{{ weatherCard.minTemp }}</div>
      <div class="weather-card__temp-max">{{ weatherCard.maxTemp }}</div>
    </div>
  </div>
</div>
